mapper.select:
	select * from rooms a left outer join participation_room b
	on a.rid = b.rid where uid = #{uid}
   union
 select * from rooms a right outer join participation_room b
	on a.rid = b.rid where uid = #{uid}

mapper.update:
update rooms set
name = #{name}, participationNum = #{participationNum}, description = #{description}
where rid = #{rid}
